import  React, {Component} from 'react';
import axios from 'axios';

import Navbar from './Navbar';
import Header from './Header';
import Footer from './Footer';
import './css/addTeacher.css';

export default  class addTeacher extends  Component{
    constructor(props) {
        super(props);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            email:'',
            password:''
        }
    }
    onChangeEmail(e){
        this.setState( {
            email: e.target.value
        });
    }
    onChangePassword(e){
        this.setState( {
            password: e.target.value
        });
    }

    onSubmit(e){
        e.preventDefault();


        const Email = this.state.email;
        let object = {
            email : this.state.email,
            password : this.state.password
        };

        if ((this.state.email === "admin") && (this.state.password === "adminMatara")) {
             const Station = "Matara";
             this.props.history.push('/adminhome/'+Station);

        }
        else if ((this.state.email === "admin") && (this.state.password === "adminGalle")) {

            const Station = "Galle";
            this.props.history.push('/adminhome/'+Station);

       }
       else if ((this.state.email === "admin") && (this.state.password === "adminColombo")) {

        const Station = "Colombo";
        this.props.history.push('/adminhome/'+Station);

       }else {
            axios.post('http://localhost:4000/trainFood/login',object)
                .then(res => {
                    if(res.data.message === "Successful Login"){
                        // alert(res.data.message)
                        // alert(Email)
                        this.props.history.push('/index/'+Email);
                    }
                    else{
                        // alert(res.data.message)
                        this.props.history.push('/signIn');
                    }

                });
        }
        

    }

    render() {
        return(
            <div>
                <Header/>
                <Navbar/>
                <div className="container " style={{marginTop:10}}>
                        <h3 className="text-center" style={{borderBottom:'tomaato solid'}}>Teacher Registration Form</h3>
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <label>Teacher Name :</label>
                                <input type ="text" required placeholder = "Please enter name" className="form-control" value={this.state.name} onChange = {this.onChangeName}/>
                            </div>
                            <div className="form-group">
                                <label>Phone Number :</label>
                                <input type ="number" required placeholder = "Please enter contact number" className="form-control" value={this.state.phone} onChange = {this.onChangePhone}/>
                            </div>
                            <div className="form-group">
                                <label>eMail Address :</label>
                                <input type ="email" required placeholder = "Please enter email" className="form-control" value={this.state.email} onChange = {this.onChangeEmail}/>
                            </div>
                            <div className="form-group">
                                <label>Subjects :</label>
                                {/* <input type ="password" required placeholder = "Please enter Password" className="form-control" value={this.state.password} onChange = {this.onChangePassword}/> */}
                                <select>
                                    <option value = "Mathematics">Mathematics</option>
                                    <option>English</option>
                                    <option>Sinhala</option>
                                    <option>Geography</option>
                                    <option>History</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <input type = "submit" value = "Register Service" className="btn btn-dark"/>
                            </div>
                        </form>
                    </div>
                <Footer/>
            </div>
        )
    }
}
